package com.example.prm_assignment.data.model;

import com.google.gson.annotations.SerializedName;

public class AppointmentModel {
    @SerializedName("_id")
    private String id;

    @SerializedName("customer_id")
    private CustomerInfo customerId;

    @SerializedName("vehicle_id")
    private VehicleInfo vehicleId;

    @SerializedName("center_id")
    private CenterInfo centerId;

    @SerializedName("staffId")
    private StaffInfo staffId;

    private String startTime;
    private String endTime;
    private String status;
    private String createdAt;
    private String updatedAt;

    public AppointmentModel() {
    }

    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public CustomerInfo getCustomerId() {
        return customerId;
    }

    public void setCustomerId(CustomerInfo customerId) {
        this.customerId = customerId;
    }

    public VehicleInfo getVehicleId() {
        return vehicleId;
    }

    public void setVehicleId(VehicleInfo vehicleId) {
        this.vehicleId = vehicleId;
    }

    public CenterInfo getCenterId() {
        return centerId;
    }

    public void setCenterId(CenterInfo centerId) {
        this.centerId = centerId;
    }

    public StaffInfo getStaffId() {
        return staffId;
    }

    public void setStaffId(StaffInfo staffId) {
        this.staffId = staffId;
    }

    public String getStartTime() {
        return startTime;
    }

    public void setStartTime(String startTime) {
        this.startTime = startTime;
    }

    public String getEndTime() {
        return endTime;
    }

    public void setEndTime(String endTime) {
        this.endTime = endTime;
    }

    public String getStatus() {
        return status;
    }

    public void setStatus(String status) {
        this.status = status;
    }

    public String getCreatedAt() {
        return createdAt;
    }

    public void setCreatedAt(String createdAt) {
        this.createdAt = createdAt;
    }

    public String getUpdatedAt() {
        return updatedAt;
    }

    public void setUpdatedAt(String updatedAt) {
        this.updatedAt = updatedAt;
    }

    public static class CustomerInfo {
        @SerializedName("_id")
        private String id;
        private String customerName;
        private String address;
        private String dateOfBirth;

        public String getId() {
            return id;
        }

        public String getCustomerName() {
            return customerName;
        }

        public String getAddress() {
            return address;
        }

        public String getDateOfBirth() {
            return dateOfBirth;
        }
    }

    public static class VehicleInfo {
        @SerializedName("_id")
        private String id;
        private String vehicleName;
        private String model;
        private String plateNumber;
        private Integer mileage;

        public String getId() {
            return id;
        }

        public String getVehicleName() {
            return vehicleName;
        }

        public String getModel() {
            return model;
        }

        public String getPlateNumber() {
            return plateNumber;
        }

        public Integer getMileage() {
            return mileage;
        }
    }

    public static class CenterInfo {
        @SerializedName("_id")
        private String id;
        private String name;
        private String address;
        private String phone;

        public String getId() {
            return id;
        }

        public String getName() {
            return name;
        }

        public String getAddress() {
            return address;
        }

        public String getPhone() {
            return phone;
        }
    }

    public static class StaffInfo {
        @SerializedName("_id")
        private String id;
        private String staffName;

        public String getId() {
            return id;
        }

        public String getStaffName() {
            return staffName;
        }
    }
}

package com.example.prm_assignment.data.model;

import java.util.List;

public class AppointmentsResponse {
    private boolean success;
    private String message;
    private AppointmentsData data;

    public AppointmentsResponse() {
    }

    public AppointmentsResponse(boolean success, String message, AppointmentsData data) {
        this.success = success;
        this.message = message;
        this.data = data;
    }

    public boolean isSuccess() {
        return success;
    }

    public void setSuccess(boolean success) {
        this.success = success;
    }

    public String getMessage() {
        return message;
    }

    public void setMessage(String message) {
        this.message = message;
    }

    public AppointmentsData getData() {
        return data;
    }

    public void setData(AppointmentsData data) {
        this.data = data;
    }

    public static class AppointmentsData {
        private List<AppointmentModel> appointments;
        private int total;
        private int page;
        private int limit;
        private int totalPages;

        public AppointmentsData() {
        }

        public List<AppointmentModel> getAppointments() {
            return appointments;
        }

        public void setAppointments(List<AppointmentModel> appointments) {
            this.appointments = appointments;
        }

        public int getTotal() {
            return total;
        }

        public void setTotal(int total) {
            this.total = total;
        }

        public int getPage() {
            return page;
        }

        public void setPage(int page) {
            this.page = page;
        }

        public int getLimit() {
            return limit;
        }

        public void setLimit(int limit) {
            this.limit = limit;
        }

        public int getTotalPages() {
            return totalPages;
        }

        public void setTotalPages(int totalPages) {
            this.totalPages = totalPages;
        }
    }
}

