package com.example.prm_assignment.data.model;

public class LoginRequest {
    private String identifier;
    private String password;

    public LoginRequest() {
    }

    public LoginRequest(String identifier, String password) {
        this.identifier = identifier;
        this.password = password;
    }

    public String getIdentifier() {
        return identifier;
    }

    public void setIdentifier(String identifier) {
        this.identifier = identifier;
    }

    public String getPassword() {
        return password;
    }

    public void setPassword(String password) {
        this.password = password;
    }
}

package com.example.prm_assignment.data.model;

public class LoginResponse {
    private boolean success;
    private String message;
    private LoginData data;

    public LoginResponse() {
    }

    public LoginResponse(boolean success, String message, LoginData data) {
        this.success = success;
        this.message = message;
        this.data = data;
    }

    public boolean isSuccess() {
        return success;
    }

    public void setSuccess(boolean success) {
        this.success = success;
    }

    public String getMessage() {
        return message;
    }

    public void setMessage(String message) {
        this.message = message;
    }

    public LoginData getData() {
        return data;
    }

    public void setData(LoginData data) {
        this.data = data;
    }

    public static class LoginData {
        private String accessToken;
        private String refreshToken;
        private UserInfo user;

        public LoginData() {
        }

        public LoginData(String accessToken, String refreshToken, UserInfo user) {
            this.accessToken = accessToken;
            this.refreshToken = refreshToken;
            this.user = user;
        }

        public String getAccessToken() {
            return accessToken;
        }

        public void setAccessToken(String accessToken) {
            this.accessToken = accessToken;
        }

        public String getRefreshToken() {
            return refreshToken;
        }

        public void setRefreshToken(String refreshToken) {
            this.refreshToken = refreshToken;
        }

        public UserInfo getUser() {
            return user;
        }

        public void setUser(UserInfo user) {
            this.user = user;
        }
    }

    public static class UserInfo {
        private String id;
        private String email;
        private String fullName;
        private String phoneNumber;
        private String avatar;

        public UserInfo() {
        }

        public UserInfo(String id, String email, String fullName, String phoneNumber, String avatar) {
            this.id = id;
            this.email = email;
            this.fullName = fullName;
            this.phoneNumber = phoneNumber;
            this.avatar = avatar;
        }

        public String getId() {
            return id;
        }

        public void setId(String id) {
            this.id = id;
        }

        public String getEmail() {
            return email;
        }

        public void setEmail(String email) {
            this.email = email;
        }

        public String getFullName() {
            return fullName;
        }

        public void setFullName(String fullName) {
            this.fullName = fullName;
        }

        public String getPhoneNumber() {
            return phoneNumber;
        }

        public void setPhoneNumber(String phoneNumber) {
            this.phoneNumber = phoneNumber;
        }

        public String getAvatar() {
            return avatar;
        }

        public void setAvatar(String avatar) {
            this.avatar = avatar;
        }
    }
}

package com.example.prm_assignment.data.model;

import com.google.gson.annotations.SerializedName;
import java.util.List;

public class ProfileResponse {
    private boolean success;
    private String message;
    private ProfileData data;

    public ProfileResponse() {
    }

    public ProfileResponse(boolean success, String message, ProfileData data) {
        this.success = success;
        this.message = message;
        this.data = data;
    }

    public boolean isSuccess() {
        return success;
    }

    public void setSuccess(boolean success) {
        this.success = success;
    }

    public String getMessage() {
        return message;
    }

    public void setMessage(String message) {
        this.message = message;
    }

    public ProfileData getData() {
        return data;
    }

    public void setData(ProfileData data) {
        this.data = data;
    }

    public static class ProfileData {
        @SerializedName("_id")
        private String id;
        private UserId userId;
        private String customerName;
        private String address;
        private String dateOfBirth;
        private List<String> deviceTokens;
        private String createdAt;
        private String updatedAt;

        public ProfileData() {
        }

        public ProfileData(String id, UserId userId, String customerName, String address, 
                          String dateOfBirth, List<String> deviceTokens, String createdAt, String updatedAt) {
            this.id = id;
            this.userId = userId;
            this.customerName = customerName;
            this.address = address;
            this.dateOfBirth = dateOfBirth;
            this.deviceTokens = deviceTokens;
            this.createdAt = createdAt;
            this.updatedAt = updatedAt;
        }

        public String getId() {
            return id;
        }

        public void setId(String id) {
            this.id = id;
        }

        public UserId getUserId() {
            return userId;
        }

        public void setUserId(UserId userId) {
            this.userId = userId;
        }

        public String getCustomerName() {
            return customerName;
        }

        public void setCustomerName(String customerName) {
            this.customerName = customerName;
        }

        public String getAddress() {
            return address;
        }

        public void setAddress(String address) {
            this.address = address;
        }

        public String getDateOfBirth() {
            return dateOfBirth;
        }

        public void setDateOfBirth(String dateOfBirth) {
            this.dateOfBirth = dateOfBirth;
        }

        public List<String> getDeviceTokens() {
            return deviceTokens;
        }

        public void setDeviceTokens(List<String> deviceTokens) {
            this.deviceTokens = deviceTokens;
        }

        public String getCreatedAt() {
            return createdAt;
        }

        public void setCreatedAt(String createdAt) {
            this.createdAt = createdAt;
        }

        public String getUpdatedAt() {
            return updatedAt;
        }

        public void setUpdatedAt(String updatedAt) {
            this.updatedAt = updatedAt;
        }
    }

    public static class UserId {
        @SerializedName("_id")
        private String id;
        private String phone;
        private String role;
        private String email;

        public UserId() {
        }

        public UserId(String id, String phone, String role, String email) {
            this.id = id;
            this.phone = phone;
            this.role = role;
            this.email = email;
        }

        public String getId() {
            return id;
        }

        public void setId(String id) {
            this.id = id;
        }

        public String getPhone() {
            return phone;
        }

        public void setPhone(String phone) {
            this.phone = phone;
        }

        public String getRole() {
            return role;
        }

        public void setRole(String role) {
            this.role = role;
        }

        public String getEmail() {
            return email;
        }

        public void setEmail(String email) {
            this.email = email;
        }
    }
}

package com.example.prm_assignment.data.remote;

import com.example.prm_assignment.data.model.LoginRequest;
import com.example.prm_assignment.data.model.LoginResponse;
import com.example.prm_assignment.data.model.ProfileResponse;
import retrofit2.http.Body;
import retrofit2.http.POST;
import retrofit2.http.GET;
import retrofit2.http.Header;

public interface AuthApi {
    @POST("auth/login-by-password")
    Object login(@Body LoginRequest request);

    @GET("auth/profile")
    Object getProfile(@Header("Authorization") String token);

    @POST("auth/logout")
    Object logout(@Header("Authorization") String token);
}

package com.example.prm_assignment.ui

import android.app.Application
import android.util.Log
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.viewModelScope
import com.example.prm_assignment.data.TokenManager
import com.example.prm_assignment.data.model.LoginRequest
import com.example.prm_assignment.data.model.ProfileData
import com.example.prm_assignment.data.remote.RetrofitClient
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch

data class AuthState(
    val isLoading: Boolean = false,
    val isLoggedIn: Boolean = false,
    val userProfile: ProfileData? = null,
    val errorMessage: String? = null,
    val successMessage: String? = null
)

class AuthViewModel(application: Application) : AndroidViewModel(application) {
    private val tokenManager = TokenManager(application.applicationContext)
    private val authApi = RetrofitClient.getAuthApi()

    private val _authState = MutableStateFlow(AuthState())
    val authState: StateFlow<AuthState> = _authState.asStateFlow()

    companion object {
        private const val TAG = "AuthViewModel"
    }

    fun login(email: String, password: String) {
        viewModelScope.launch {
            try {
                Log.d(TAG, "Starting login for email: $email")
                _authState.value = _authState.value.copy(isLoading = true, errorMessage = null)

                val request = LoginRequest(
                    identifier = email,
                    password = password
                )

                Log.d(TAG, "Calling login API...")
                val response = authApi.login(request)
                Log.d(TAG, "Login API response: success=${response.success}, message=${response.message}")

                if (response.success && response.data != null) {
                    // Lưu token
                    Log.d(TAG, "Saving tokens...")
                    tokenManager.saveTokens(response.data.accessToken, response.data.refreshToken)
                    Log.d(TAG, "Tokens saved successfully")

                    // Lấy thông tin user trước khi set isLoggedIn = true
                    try {
                        Log.d(TAG, "Fetching user profile...")
                        val profileResponse = authApi.getProfile("Bearer ${response.data.accessToken}")
                        Log.d(TAG, "Profile API response: success=${profileResponse.success}")

                        val userProfile = if (profileResponse.success && profileResponse.data != null) {
                            Log.d(TAG, "User profile: ${profileResponse.data}")
                            profileResponse.data
                        } else {
                            Log.w(TAG, "Profile response failed or data is null")
                            null
                        }

                        // Chỉ set isLoggedIn = true sau khi đã có thông tin user
                        Log.d(TAG, "Setting isLoggedIn = true")
                        _authState.value = _authState.value.copy(
                            isLoggedIn = true,
                            isLoading = false,
                            userProfile = userProfile,
                            successMessage = "Đăng nhập thành công!"
                        )
                        Log.d(TAG, "Login completed successfully")
                    } catch (profileException: Exception) {
                        Log.e(TAG, "Error fetching profile", profileException)
                        // Vẫn cho đăng nhập thành công nhưng không có profile
                        _authState.value = _authState.value.copy(
                            isLoggedIn = true,
                            isLoading = false,
                            userProfile = null,
                            successMessage = "Đăng nhập thành công!"
                        )
                    }
                } else {
                    Log.w(TAG, "Login failed: ${response.message}")
                    _authState.value = _authState.value.copy(
                        isLoading = false,
                        errorMessage = response.message ?: "Đăng nhập thất bại"
                    )
                }
            } catch (e: java.net.ConnectException) {
                Log.e(TAG, "Connection exception", e)
                _authState.value = _authState.value.copy(
                    isLoading = false,
                    errorMessage = "Không thể kết nối đến server. Vui lòng kiểm tra:\n1. Backend đã chạy chưa?\n2. Port đúng chưa? (hiện tại: 8080)"
                )
            } catch (e: java.net.SocketTimeoutException) {
                Log.e(TAG, "Socket timeout exception", e)
                _authState.value = _authState.value.copy(
                    isLoading = false,
                    errorMessage = "Kết nối timeout. Server không phản hồi."
                )
            } catch (e: Exception) {
                Log.e(TAG, "Unexpected exception during login", e)
                _authState.value = _authState.value.copy(
                    isLoading = false,
                    errorMessage = "Lỗi: ${e.localizedMessage ?: e.message}\n${e.stackTraceToString()}"
                )
            }
        }
    }

    private suspend fun fetchUserProfile(token: String) {
        try {
            val response = authApi.getProfile(token)
            if (response.success && response.data != null) {
                _authState.value = _authState.value.copy(
                    userProfile = response.data
                )
            }
        } catch (e: Exception) {
            // Xử lý lỗi lấy profile
        }
    }

    fun logout() {
        viewModelScope.launch {
            try {
                _authState.value = _authState.value.copy(isLoading = true)

                val token = tokenManager.getAccessToken()
                if (token != null) {
                    authApi.logout("Bearer $token")
                }

                tokenManager.clearTokens()
                _authState.value = AuthState(successMessage = "Đã đăng xuất")
            } catch (e: Exception) {
                _authState.value = _authState.value.copy(
                    isLoading = false,
                    errorMessage = "Lỗi đăng xuất: ${e.message}"
                )
            }
        }
    }

    fun loadUserProfile() {
        viewModelScope.launch {
            val token = tokenManager.getAccessToken()
            if (token != null) {
                fetchUserProfile("Bearer $token")
            }
        }
    }

    fun clearMessages() {
        _authState.value = _authState.value.copy(errorMessage = null, successMessage = null)
    }
}

